import React, { Component } from 'react';
import Dropzone from 'react-dropzone';
import './Classifier.css';
import {Spinner, Button} from 'react-bootstrap';
import axios from 'axios';

class Classifier extends Component {
    state = { 
        files: [],
        isLoading: false,
    }

    // componentDidMount() {
    //     this.getImages()
    // }

    // getImages = () => {
    //     axios.get('http://localhost:8000/api/images/', {
    //         headers: {
    //             'accept': 'application/json'
    //         }
    //     }).then(resp=>{
    //         console.log(resp)
    //     })
    // }

    onDrop = (files) => {
        this.setState({
            files: [],
            isLoading: true,
            })
        this.loadImage(files)
    }

    loadImage = (files) => {
        setTimeout(() => {
            this.setState({
                files,
                isLoading: false
            })
        }, 1000);
        // console.log('image loaded');
        // console.log(files);
    }

    sendImage = () => {
        let formData = new FormData();
        formData.append('image_field', this.state.files[0], this.state.files[0].name);
        axios.post('http://localhost:8000/api/images/', formData, {
            headers: {
                'accept': 'application/json',
                'content-type': 'multipart/form-data'
            }
        })
        .then(resp=>{
            console.log(resp.data.id);
            this.getClass(resp);
        })
        .catch(err=>{
            console.log('Error message: ' + err);
        })
    }

    getClass = () => {
        axios.get('http://localhost:8000/api/images/${obj.data.id}/')
    }

    render() { 
        const files = this.state.files.map(file => (
            <li key={file.name}>
                {file.name} - {file.size} bytes
            </li>
        ));

        return ( 
            <Dropzone onDrop={this.onDrop} accept='image/png, image/jpeg'>
            {({getRootProps, getInputProps}) => (
            <section className="container">
                <div {...getRootProps({className: 'dropzone back'})}>
                    <input {...getInputProps()} />
                    <i className="far fa-image mb-2 dropbox_text" style={{fontSize:100}}></i>
                    <p className='dropbox_text'>Drag 'n' drop some files here, or click to select files</p>
                </div>
                <aside>
                    {files}
                </aside>

                {this.state.files.length > 0 &&
                <Button variant='info' size='lg' className='mt-3' onClick={this.sendImage}>Select Image</Button>
                }
                
                {this.state.isLoading &&
                <Spinner animation="grow" role="status"></Spinner>
                }

            </section>
            )}
            </Dropzone>
        );
    }
}
 
export default Classifier;
