import React, { Component } from 'react';
import axios from 'axios';
import {Card} from 'react-bootstrap';


class ClassificationHistory extends Component {
    state = {
        images: [],
    }

    componentDidMount() {
        this.getImages()
    }

    getImages = () => {
        axios.get('http://localhost:8000/api/images/', {
            headers: {
                'accept': 'application/json'
            }
        }).then(res=>{
            this.setState({images: res.data})
            console.log(res)
        })
    }

    render() {
        const images = this.state.images.map(image => {
            return ( 
                <Card className='bg-dark text-white mx-auto' style={{ width: '18rem' }}>
                    <Card.Body>
                        <Card.Text>
                            <h3 key={img.id}>Here will be an image and some text</h3>
                            {this.state.userImage.data.classification}
                        </Card.Text>
                    </Card.Body>
                    <Card.Img variant="bottom" src={this.state.userImage.data.image_field} />
                </Card>
            );
        })
        return (
            <div>
            <h1>Image History here</h1>
            {images}
            </div>
        );
    }
}
 
export default ClassificationHistory;
