import React, { Component } from 'react';
import Dropzone from 'react-dropzone';
import './Classifier.css';
import {Spinner} from 'react-bootstrap';
import axios from 'axios';
import { Button } from 'bootstrap';

class Classifier extends Component {
    state = { 
        files: [],
        isLoading: false,
    }

    // componentDidMount() {
    //     this.getImages()
    // }

    // getImages = () => {
    //     axios.get('http://localhost:8000/api/images/', {
    //         headers: {
    //             'accept': 'application/json'
    //         }
    //     }).then(resp=>{
    //         console.log(resp)
    //     })
    // }

    onDrop =(files) => {
        this.setState({
            isLoading: true,
            })
        this.loadImage(files)
    }

    loadImage=(files)=>{
        setTimeout(() => {
            this.setState({
                files,
                isLoading: false
            })
        }, 1000);
    }

    // https://react-dropzone.js.org/
    render() { 
        const files = this.state.files.map(file => (
            <li key={file.name}>
                {file.name} - {file.size} bytes
            </li>
        ));
        return ( 
            <Dropzone onDrop={this.onDrop} accept='image/jpg, image/png'>
            {({getRootProps, getInputProps}) => (
            <section className="container">
                <div {...getRootProps({className: 'dropzone back'})}>
                    <input {...getInputProps()} />
                    <i className="far fa-image mb-2 dropbox_text" style={{fontSize:100}}></i>
                    <p className='dropbox_text'>Drag 'n' drop some files here, or click to select files</p>
                </div>
                <aside>
                    {files}
                </aside>

                {this.state.files.length > 0 && // only show button when image is selected
                <Button variant='info' size='lg' className='mt-3'>Select Image</Button>
                }
                
                {this.state.isLoading &&
                <Spinner animation="grow" role="status"></Spinner>
                }

            </section>
            )}
            </Dropzone>
        );
    }
}
 
export default Classifier;
